import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Program = () => {
  const { id } = useParams();
  const [program, setProgram] = useState({});
  useEffect(() => {
    fetch(`https://api.tvmaze.com/shows/${id}`)
    .then(response=> response.json())
    .then(data=> setProgram(data) )
  }, [id]);

  const {image, name, genres, summary, network} = program || {};
  const {country, name:networkname, id:networkid} = network || {};
  const{code, name:countryname, timezone} = country ||{};
  console.log(network);
  //const genre = [genres.join(", ")];
  var {medium: picture} = image || {};
  return (
        <div style={{boxSizing:'border-box',display:'flex', flexWrap:'wrap'}}>
        <div style={{flex:'10%'}}>
        <img  src={picture} alt="loading" />
        </div> 
        <div style={{flex:'70%', padding:'2%'}}>
        <h4 style={{textAlign:'center'}}>{name}</h4>
        <p  dangerouslySetInnerHTML={{__html: summary}} />
        <p><strong>Genre: </strong><span style={{textDecoration:'underline'}}>{genres ? [genres.join(", ")] : genres }</span></p>
        <p><strong>Country-origin: </strong>{countryname} <strong>Netwrok: </strong>{networkname} </p>
        </div>
        </div>
  );
};
export default Program;
