import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Maincard = () => {
  const [programs, setTodos] = useState([]);
  const navigate = useNavigate();
  useEffect(() => {
    fetch(`https://api.tvmaze.com/shows`)
    .then(response=> response.json())
    
    .then(data=> setTodos(data) )
  }, []);

//console.log(todos);    
  return (
    <>
    <div style={{boxSizing:'border-box', display: "flex", flexWrap: "wrap", border:'2px solid pink',backgroundColor:'lightgrey'}}>
        {programs.map((program)=>{
          const {image:picture, name, id, genres} = program;
          const{medium} = picture;
          async function handleClick() {
              navigate(`/${id}`);
            
          }
            return (
              <div key={id} onClick={handleClick} style={{flex:'25%', border:'2px solid white', textAlign: 'center'}}>
                <img src= {medium} alt='see here for' style={{padding:'10% 25%'}}/>
                <h4>{name}</h4>
                <h6>{[genres.join(", ")]}</h6>
              </div>
            )
          })}
    </div>
    <a href="#">Back to top</a>
    </>
    
  );
};
export default Maincard;
