import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import './Style.css';
const Maincard = () => {
  const [programs, setTodos] = useState([]);
  const [searchmovie, setSearchmovie] = useState("");
  const navigate = useNavigate();
  useEffect(() => {
    fetch(`https://api.tvmaze.com/shows`)
    .then(response=> response.json())
    .then(data=> setTodos(data) )
  }, []);

//console.log(todos);    
  return (
    <>
    <div className="firstdiv">
    <input type="text" placeholder="Search movie here..."  style={{display:'block', marginBottom:'20px'}}
    onChange={(event)=>setSearchmovie(event.target.value)}/>
        {programs.filter((val)=>{
          if(searchmovie ===""){return val}
          else if(val.name.toLowerCase().includes(searchmovie.toLowerCase())===true){
            if(val==={}){
              return val
            }else{
              return (<p>No results found</p>)
            }
          }
        }).map((program)=>{
          const {image:picture, name, id, genres} = program;
          const{medium} = picture;
          async function handleClick() {
              navigate(`/${id}`);
          }
            return (
              <div key={id} onClick={handleClick} className="secondiv" >
                <img src= {medium} style={{display:'inline-block', boxSizing:'border-box', 
                width:'90%',marginBottom:'10px'}} alt='see here for'/>
                <h style={{fontSize:'20px'}}>{name}</h><br/>
                <h style={{fontSize:'15px'}}>{[genres.join(" | ")]}</h>
              </div>
            )
          })}
    </div>
    <a href="#">Back to top</a>
    </>
    
  );
};
export default Maincard;
